'use strict'
const YAML = require('yamljs')
const fs = require('fs')
const stream = require('stream')
const through = require('through2')

const App = {}

function jsonToScssVars (obj) {
  let scssVars = ''

  for (let i in obj) {

    scssVars += '$drupal_breakpoint_' + obj[i].label + '_width: ' + obj[i].scssMinWidth + 'px;\n'
  }

  return scssVars
}

App.read = function (path) {
  let rs = stream.Readable()
  let breakpoints = YAML.load(path)

  rs._read = function () {
    rs.push(jsonToScssVars(breakpoints))
    rs.push(null)
  }

  return rs
}

App.write = function (path) {
  let scssFile = fs.createWriteStream(path)
  return scssFile
}

App.ymlToScss = function () {
  return through.obj(function (file, enc, cb) {
    var content = file.contents.toString('utf8')
    var parsedYaml = YAML.parse(content)
    file.contents = new Buffer(String(jsonToScssVars(parsedYaml)))
    cb(null, file)
  })
}

module.exports = App
